  void DenmPort::outgoing_send(const LibItsDenm__TypesAndValues::DenmReq &send_par) {
    //    loggers::get_instance().log_msg(">>> DenmPort::outgoing_send: payload=", send_par);

    float duration;
    loggers::get_instance().set_start_time(_time_key);
    params params;
    static_cast<denm_layer *>(_layer)->sendMsg(send_par, params);
    loggers::get_instance().set_stop_time(_time_key, duration);
  }

  void DenmPort::receiveMsg(const LibItsDenm__TypesAndValues::DenmInd &p_ind, const params &p_params) {
    //    loggers::get_instance().log_msg(">>> DenmPort::receive_msg: ", p_ind);

    incoming_message(p_ind);
  }

